import React from 'react'
import './banner.scss'
import KeyboardArrowLeftIcon from '@mui/icons-material/KeyboardArrowLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';

const Banner = () => {
    const data = [
        "https://images.pexels.com/photos/1549200/pexels-photo-1549200.jpeg?auto=compress&cs=tinysrgb&w=1600",
        "https://images.pexels.com/photos/949670/pexels-photo-949670.jpeg?auto=compress&cs=tinysrgb&w=1600",
        "https://images.pexels.com/photos/837140/pexels-photo-837140.jpeg?auto=compress&cs=tinysrgb&w=1600",
      ];
      const sliderFunc=()=>{

      }
  return (
    <div className='banner-container'>
        <div className="banner-wrapper">
          {data.map((item)=>  
                <img src={item} alt="" className='banner-img' style={{transform:'translateX(-100)'}}/>
          )}
    
        </div>
        <div className="icons">
          <div className="icon" onClick={()=>sliderFunc("left")}><KeyboardArrowLeftIcon/></div>
          <div className="icon" onClick={()=>sliderFunc("right")}><ChevronRightIcon/></div>
        </div>
    </div>
  )
}

export default Banner
