import React ,{useState}from 'react'
import './product.scss'
import LocalMallIcon from '@mui/icons-material/LocalMall';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ContentPasteSearchIcon from '@mui/icons-material/ContentPasteSearch';


const Product = () => {
  const data = [
    'https://assets.ajio.com/medias/sys_master/root/20220901/Tthc/6310ee67f997dd1f8dd3e386/-473Wx593H-464918983-blue-MODEL2.jpg',
    'https://assets.ajio.com/medias/sys_master/root/20220901/HOL6/6310ee7aaeb269dbb361c4e1/-473Wx593H-464919054-green-MODEL.jpg'
  ]
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(1);
  const toggleImage =()=>{
    if (selectedImage===1){
      selectedImage(0)
    }
    else{
      selectedImage(1)
    }
  }
  const handleQuantity =(type)=>{
    if(type==='less'){
      setQuantity((pre)=>quantity===1? quantity: quantity-1)
    }
    else{
      setQuantity((pre)=>quantity+1)

    }
  }
  console.log(quantity)
  return (
    <div className='product'>
      <div className="wrapper">
        <div className="left">
          <div className="first">
            <img src={data[0]} alt="" onClick={toggleImage}/>
            <img src={data[1]} alt="" />
          </div>
          <div className="second">
            <img src={data[selectedImage]} alt="" />
          </div>
        </div>
        <div className="right">
          <h2>Half Sleeve T-shirt</h2>
          <h3>599 RS</h3>
          <p>Lorem ipsum dolor sit amet consectetur, adipisicing elit. Distinctio reprehenderit molestiae dolores modi voluptatum. Odit qui quibusdam eius optio necessitatibus?</p>
          <div className="quantity">
            <button onClick={()=>{handleQuantity("less")}}>-</button>
            <span>{quantity}</span>
            <button onClick={()=>{handleQuantity("add")}}>+</button>
          </div>
          <button className='addtocart'>
            <LocalMallIcon /> Add to Cart</button>
          <span className="addTowishList">
            <FavoriteBorderIcon />
            <p>Add To Wishlist</p>
          </span>
          <span className="addTocompare">
            <ContentPasteSearchIcon />
            <p>Add To Compare </p>
          </span>
        </div>


      </div>
    </div>
  )
}

export default Product
