import { createSlice } from '@reduxjs/toolkit'

const initialState ={
    cart:[]
}

export const cartReducer = createSlice({initialState},{
    addToCart:(state,action)=>{
        state.cart= [...state.cart,'produc']
        console.console.log("adding to cart");
    }
})
