import { createSlice } from '@reduxjs/toolkit'

const initialState = []


export const cartReducer = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        addToCart: (state, action) => {
            state = [...state, action.payload]
        }
    }
})

export const { addToCart } = cartReducer.actions
export default cartReducer.reducer
