 import axios from 'axios'
 import React,{useEffect,useState} from 'react'
const useFetch =(url)=>{
    useEffect(async() => {
        const response  = await axios.get(url)
        const data = response.data
        return data  
    }, [url]);
     
}
export default useFetch;
