import React ,{useState}from 'react'
import './product.scss'
import LocalMallIcon from '@mui/icons-material/LocalMall';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import ContentPasteSearchIcon from '@mui/icons-material/ContentPasteSearch';
import { useDispatch } from 'react-redux';
import { addToCart } from '../../Redux/reducers';
import {data} from '../../data'
import { useParams } from 'react-router-dom';



const Product = () => {
  const {id} =useParams();
  const dispatch = useDispatch();
  const item = data.find((e)=>{return e.id===parseInt(id)})
  // const data1 = [
  //   'https://assets.ajio.com/medias/sys_master/root/20220901/Tthc/6310ee67f997dd1f8dd3e386/-473Wx593H-464918983-blue-MODEL2.jpg',
  //   'https://assets.ajio.com/medias/sys_master/root/20220901/HOL6/6310ee7aaeb269dbb361c4e1/-473Wx593H-464919054-green-MODEL.jpg'
  // ]
  const [quantity, setQuantity] = useState(1);
  const [selectedImage, setSelectedImage] = useState(1);

  const handleQuantity =(type)=>{
    if(type==='less'){
      setQuantity((pre)=>quantity===1? quantity: quantity-1)
    }
    else{
      setQuantity((pre)=>quantity+1)

    }
  }
 const addToCartFunc=()=>{
  dispatch(addToCart({
    id:3,
    title:'',
    price:59,
    quantity:quantity,
    dis:'3erewrewr'
  }))
 }
  return (
    <div className='product'>
      <div className="wrapper">
        <div className="left">
          <div className="first">
            <img src={item?.img} alt="" onClick={()=>{setSelectedImage(0)}}/>
            <img src={data[1]} alt="" onClick={()=>{setSelectedImage(1)}} />
          </div>
          <div className="second">
            <img src={item?.img} alt="" />
          </div>
        </div>
        <div className="right">
          <h2>{item?.title}</h2>
          <h3>{item?.price} RS</h3>
          <p>{item?.disc}</p>
          <div className="quantity">
            <button onClick={()=>{handleQuantity("less")}}>-</button>
            <span>{quantity}</span>
            <button onClick={()=>{handleQuantity("add")}}>+</button>
          </div>
          <button className='addtocart' onClick={addToCartFunc}>
            <LocalMallIcon /> Add to Cart</button>
          <span className="addTowishList">
            <FavoriteBorderIcon />
            <p>Add To Wishlist</p>
          </span>
          <span className="addTocompare">
            <ContentPasteSearchIcon />
            <p>Add To Compare </p>
          </span>
        </div>
      </div>
    </div>
  )
}

export default Product
